import { FormControl, FormGroup, Validators } from "@angular/forms";
import {
  MAT_DIALOG_DATA,
  MatDialog,
  MatDialogRef,
} from "@angular/material/dialog";

import { Component } from "@angular/core";
import { ContractService } from "src/app/services/contract.service";
import { LoginComponent } from "src/app/popup/login/login.component";
import { Router } from "@angular/router";

@Component({
  selector: "app-home-page",
  templateUrl: "./home-page.component.html",
  styleUrls: ["./home-page.component.scss"],
})
export class HomePageComponent {
  constructor(
    private router: Router,
    public dialog: MatDialog,
    private contractService: ContractService
  ) {}
  loginAddress: string = "";
  loginForm = new FormGroup({
    name: new FormControl("", [Validators.required]),
    // address: new FormControl('', [Validators.required]),
  });
  gamStatus: any;

  login() {
    this.contractService.openMetamask().then((resp) => {
      this.loginAddress = resp;
      this.contractService.loader$.next(false);
    });
  }

  onSubmit() {
    this.contractService.joinGame(this.loginForm.value).then((resp: any) => {
      this.contractService.loader$.next(false);
      this.router.navigateByUrl(`/game/${this.contractService.parseData(resp.events.GameInitiated ?? resp.events.GameEnded).id}`);
    });
  }
}
<div class="page-wrapper-everything">
  <div class="content-wrapper">
    <div class="header">
      <img src="https://coindcx.com/assets/new-home-pagee/Logo_CoinDCX.svg" />
      <button (click)="login()" class="login-btn">Login</button>
    </div>
    {{ loginAddress }}
    <div class="footer">
      <div class="form-parent-wrapper">
        <div class="form-child-wrapper">
          <form
            [formGroup]="loginForm"
            class="form-wrapper"
            (ngSubmit)="onSubmit()"
          >
            <div class="label-input-wrapper">
              <label>Enter Your Name</label>
              <input formControlName="name" type="text" />
            </div>

            <button type="submit" [disabled]="!loginForm.valid">Join</button>
          </form>
        </div>
        <div class="message">{{ gamStatus }}</div>
      </div>
      <div class="footer-content">
        <h1 class="game-name">TIC-TAC-TOE</h1>
        <h2 class="dcx-name">presented by coinDCX!!!..</h2>
      </div>
      <div class="dashboard">
        <h3>
          <u>Leaderboard</u>
        </h3>
        <table class="table-wrapper">
          <tbody>
            <tr>
              <th>GameId</th>
              <th>Status</th>
            </tr>
            <tr>
              <td>1</td>
              <td>false</td>
            </tr>
            <tr>
              <td>2</td>
              <td>true</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>
</div>
