import { Injectable } from '@angular/core';
import Web3 from 'web3';
import CONSTANTS from '../constant';
import { BehaviorSubject } from "rxjs";
import { Router } from "@angular/router";

declare const window: any;
@Injectable({
  providedIn: 'root',
})
export class ContractService {
  public loader$ = new BehaviorSubject<boolean>(false);

  public moveIndex$ = new BehaviorSubject<any>(null);

  window: any;
  gameID: any;

  constructor(private router: Router) {
    window.web3 = new Web3(window.ethereum);

    let options = {
      filter: {
          value: [],
      },
      fromBlock: 0
    }
    this.getContract().events.GameEnded(options)
    .on('data', (response: any) => {
      console.log(response.events.returnValues, "Ended");
      let gameState = this.parseData(response.events).gameState
      alert(`Game State: ${gameState}`)
      this.router.navigateByUrl(`/home`);
    });

    this.getContract().events.GameStarted(options)
    .on('data', (response: any) => this.router.navigateByUrl(`/game/${this.parseData(response?.events?.GameStarted)}`));

    this.getContract().events.GameInitiated(options)
    .on('data', (response: any) => this.router.navigateByUrl(`/game/${this.parseData(response?.events?.GameInitiated)}`));

    this.getContract().events.PlayerMoved(options)
    .on('data', (response: any) => this.moveIndex$.next(this.parseData(response.events.PlayerMoved)));
  };


  private getAccounts = async () => {
    try {
      return await window.ethereum.request({ method: 'eth_accounts' });
    } catch (e) {
      return [];
    }
  };

  private getContract = () => {
    return new window.web3.eth.Contract(
      CONSTANTS.abi,
      CONSTANTS.contractAddress
    );
  };


  // Event can be GameStarted/GameInitiated/GameEnded/PlayerMoved
  public parseData = (event: any) => {
    let players = event.returnValues?.room?.players;
    let values = event.returnValues;
    return {
      'id': values.gameId,
      'name': players[0].playerName + "vs" + players[1].playerName ?? "_",
      'currentPlayerIndex': values.room.activePlayer,
      'currentPlayerName': players[values.room.activePlayer ?? 0],
      'winnerName': values.room.winnerPlayer.playerName,
      'player1': players[0].playerName,
      'player2': players[1].playerName,
      'gameState': this.getState(values.room.gameState),
      'moveIndex': values.moveIndex,
      'moveValue': values.room.activePlayer == 0 ? "X" : "0",
    };
  }

  private getState = (state: number) => {
    switch (state) {
      case 0:
        return "NOT STARTED";
      case 1:
        return "RUNNING";
      case 2: 
        return "GAME ENDED WITH WINNER";
      default:
        return "DRAW";      
    }
  }

  public openMetamask = async () => {
    this.loader$.next(true);
    window.web3 = new Web3(Web3.givenProvider || 'http://localhost:7545');
    let addresses = await this.getAccounts();
    if (!addresses.length) {
      try {
        addresses = await window.ethereum.enable();
      } catch (e) {
        console.log("Unable to fetch address");
        return false;
      }
    }
    return addresses.length ? addresses[0] : null;
  };

  public joinGame = async (data: any) => {
    this.loader$.next(true);
    let accaddress = await this.getAccounts();
    try {
      // TODO: Loading State - true;
      const response = await this.getContract().methods
        .joinRoom({ playerAddress: accaddress[0], playerName: data.name })
        .send({ from: accaddress[0] });

      // TODO: Loading State - false;

      this.gameID = response?.events?.GameStarted?.returnValues?.gameId;

      // Route to 
      return this.gameID;
    } catch (error) {
      const errorMessage = error;
    }
  };

  public move = async (data: any) => {
    this.loader$.next(true);
    let accaddress = await this.getAccounts();
    try {
      // TODO: Loading State - true;
      const response = await this.getContract().methods
        .move(this.gameID, data)
        .send({ from: accaddress[0] });
      // TODO: Loading State - false;
      console.log("Player moved on ", response?.events?.PlayerMoved?.returnValues?.moveIndex);

      return response?.events?.PlayerMoved?.returnValues?.moveIndex;
    } catch (error) {
      const errorMessage = error;
    }
  };

  public ping = async (data: any) => {
    let accaddress = await this.getAccounts();
    try {
      // TODO: Loading State - true;
      const response = await this.getContract().methods
        .updateRoomIfExpired(this.gameID)
        .send({ from: accaddress[0] });
      // TODO: Loading State - false;
      console.log("Game State", response?.events?.PlayerMoved?.returnValues?.room.state);
    } catch (error) {
      const errorMessage = error;
    }
  };

  public isGameRunning = async (gameId: number) => {
    try {
      let accaddress = await this.getAccounts();
      if(!!accaddress) return false;  

      const response = await this.getContract().methods
        .isGameRunning(this.gameID)
        .call({ from: accaddress[0] });
      console.log("res", response);
      return response;
    } catch (error) {
      console.log(error);
      return false;
    }
  };

  public isGameValid = async (gameId: number) => {
    try {
      let accaddress = await this.getAccounts();
      if(!!accaddress) return false;  

      const response = await this.getContract().methods
        .totalGames(this.gameID)
        .call({ from: accaddress[0] });
      return gameId <= response;
    } catch (error) {
      console.log(error);
      return false;
    }
  };
}
